import React,{useEffect,useState} from "react";
import { NavLink } from "react-router-dom";
import navLinks from '../../assets/dummy data/navLinks'
import "./Sidebar.css";
import { Link } from "react-router-dom";
import profileImg from '../../assets/images/images-2.jpeg';
import logo from '../../assets/images/logo1.jpeg';
import MyProfile from "../../Pages/MyProfile";






const Sidebar = ({}) => {

    const [fnames, SetFnames]=useState([])
    const [specificValue, setSpecificValue]=useState('')
    useEffect(()=>{
        fetch("http://localhost:8080/user/getAll")
        .then(res=>res.json())
        .then((result)=>{ 
            const fieldValue =result.fieldName;
            setSpecificValue(fieldValue);
        SetFnames(result);
      }
      )
    },[])

    return  (
    <div className="sidebar">


            <div className="logo">

               <img src={logo} alt="" className="img1"/>

            </div>


            <div className="profile">            {/*profile*/}
                    <span>
                        <Link to ='/myprofile'><img src={profileImg} alt=""/></Link>
                    </span>
                          <h3>
                          {fnames.map(user=>( 
                            <p> Hello,{user.fname}</p>
                          ))}

                          </h3>
            </div>


        <div className ="sidebar__content" >        {/*sidebar content*/}
            <div className="menu">
                <ul className="nav__list">
                    {navLinks.map((item,index) => (
                    <li className="nav__item" key={index}>
                        <NavLink 
                            to = {item.path}
                            className={(navClass) =>
                                navClass.isActive ? "nav__active nav__link" : "nav__link"
                            }
                            >
                                <i className={item.icon}></i>

                            {item.Display}

                        </NavLink>
                    </li>
                    ))}
                </ul>
            </div>

            <div className="sidebar__bottom">            {/*sidebar bottom*/}
                <span>
                    <i className="ri-logout-circle-r-line"></i> Logout
                </span>
            </div>
        </div>
    </div>
    );
};
export default Sidebar;
