import React, { useState } from "react";
import "../styles/myprofile.css";

const myStyle = {
  color: "white",
  backgroundColor: "DarkBlue",
  padding: "20px",
  fontFamily: "Sans-Serif"

};

const MyProfile = () => {
  const[fname, setFname]=useState('')
  const[lname, setLname]=useState('')
  const[nic, setNic]=useState('')
  const[email, setEmail]=useState('')
  const[mnumber, setMnumber]=useState('')
  const[address, setAddress]=useState('')
  const[city, setCity]=useState('')
  const handleClick=(e)=>{
    e.preventDefault()
    const user={fname,lname,nic,email,mnumber,address,city}
    console.log(user)
    fetch("http://localhost:8080/user/add" , {
      method:"POST",
      headers:{"Content-Type":"application/json"},
      body:JSON.stringify(user)
    }).then(()=>{
      console.log("New User Added")
    })


  }


    return(
        <div className="myprofile">
            <h2 className="myprofile__title">My Profile</h2>

            <div className="myprofile__wrapper">


        <div className="details__form">        {/*form to collect data*/}
          <h2 className="profile__title">Your Details</h2>
          <p className="profile__desc">

          </p>
          <form onSubmit={handleClick}>
            <div className="form__group">

            <div>
                <label>First Name</label>
                <input type="text" placeholder=""
                value={fname}
                onChange={(e)=>setFname(e.target.value)}/>  
              </div>
              <label>First Name Can't be Empty</label>

              <div>
                <label>Last Name</label>
                <input type="text" placeholder="" 
                value={lname}
                onChange={(e)=>setLname(e.target.value)}/>
              </div>
              <label>Last Name Can't be Empty</label>

              </div>

              <div className="form__group">

              <div>
                <label>NIC</label>
                <input type="text" placeholder=""
                value={nic}
                onChange={(e)=>setNic(e.target.value)} />
              </div>
              <label>First NIC Can't be Empty</label>


              <div>
                <label>Email</label>
                <input type="email" placeholder="" 
                value={email}
                onChange={(e)=>setEmail(e.target.value)}/>
              </div>
              <label>First Email Can't be Empty</label>

            </div>


            <div className="form__group">
            <div>
                <label>Mobile Number</label>
                <input type="text" placeholder="" 
                value={mnumber}
                onChange={(e)=>setMnumber(e.target.value)}/>
             </div>
             <label>Mobile Number Can't be Empty</label>


              <div>
                <label>Address</label>
                <input type="text" placeholder="" 
                value={address}
                onChange={(e)=>setAddress(e.target.value)}/>
              </div> 
              <label>First Address Can't be Empty</label>

            </div>

            <div className="form__group">
              <div>
                <label>City</label>
                <input type="" placeholder="" 
                value={city}
                onChange={(e)=>setCity(e.target.value)}/>
              </div>
              <label>First City Can't be Empty</label>

            </div>

            <div className="form__group">



              <div>

                <label>Your Photo</label>
                <p className="profile-img__desc">
                  This will be displayed in your profile
                </p>
                <input type="file" placeholder="choose file" />   {/*uploading the profile image*/}
              </div>

              <div className="profile__img-btns">
                <button style ={myStyle} onClick={handleClick} className="update__btn">Save </button>

              </div>
            </div>
          </form>
        </div>
       </div>
      </div>
    );
};
export default MyProfile;
