// Function to create a row of data with given parameters
//function createData(orderId, description, serviceName , price, pickupDate, deliveryDate, status) {
  //return { orderId, description, serviceName , price, pickupDate, deliveryDate, status };
//}



// Function to set style based on status
const makeStyle=(status)=>{
  if(status === 'Approved')
  {
     return{
      background: 'rgb(145 254 159 / 47%)',
      color: 'green'
     }
  }
  else if(status === 'Pending')
  {
    return{
      background: '#ffadad8f',
      color: 'red',
    }
  }
  else{
    return{
      background: '#59bfff',
      color: 'white',
    }
  }
}

// React component for displaying a basic table with the rows of data

class BasicTable extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      description: '',
      service_name: '',
      price: '',
      pickup_date: '',
      delivery_date: '',
      status: '',
      order_statuses: [],
    };
  }

  componentDidMount() {
    fetch("http://localhost:8080/order_status/getAll")
      .then(res => res.json())
      .then(result => {
        this.setState({ order_statuses: result });
      });
  }

  makeStyle(status) {
    // Add your custom style logic based on the status of the order
    // Return the appropriate style object
    return {};
  }

  render() {
    const { order_statuses } = this.state;

    return (
      <div className="Table">
        <h2>Order Status</h2>

        <TableContainer component={Paper} style={{ boxShadow: '0px 13px 20px 0px #80808029' }}>
          <Table sx={{ minWidth: 650 }} aria-label="simple table">
            <TableHead>
              <TableRow>
                <TableCell>Ord.Id</TableCell>
                <TableCell align="left">Description</TableCell>
                <TableCell align="left">Service Name</TableCell>
                <TableCell align="left">Price (Rs.)</TableCell>
                <TableCell align="left">Pickup Date</TableCell>
                <TableCell align="left">Delivery Date</TableCell>
                <TableCell align="left">Status</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {order_statuses.map(order_status => (
                <TableRow
                  key={order_status.name}
                  sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
                >
                  <TableCell component="th" scope="row">
                    {order_status.id}
                  </TableCell>
                  <TableCell align="left">{order_status.description}</TableCell>
                  <TableCell align="left">{order_status.service_name}</TableCell>
                  <TableCell align="left">{order_status.price}</TableCell>
                  <TableCell align="left">{order_status.pickup_date}</TableCell>
                  <TableCell align="left">{order_status.deliver_date}</TableCell>
                  <TableCell align="left">
                    <span className="status" style={this.makeStyle(order_status.status)}>
                      {order_status.status}
                    </span>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      </div>
    );
  }
}

export default BasicTable;
